 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5186,'catchment_area','drainage_system_current','mixed_system','mixed_system','','Mischsystem','','systeme_unitaire','','sistema_misto','','','','yyy_Schmutz- und Regenabwasser sind an das Mischabwassernetz angeschlossen. Verbindung zu SW/MW-Knoten obligatorisch, Verbindung zu RW-Knoten nicht zulässig.','Schmutz- und Regenabwasser sind an das Mischabwassernetz angeschlossen. Verbindung zu SW/MW-Knoten obligatorisch, Verbindung zu RW-Knoten nicht zulässig.','Les eaux usées et les eaux pluviales sont raccordées au réseau d’évacuation des eaux unitaire. Les liaisons avec les nœuds d’EU et d’EM sont obligatoires, celle avec les nœuds EP interdites.','Le acque luride e le acque meteoriche non infiltrabili o non evacuate direttamente in un ricettore devono esser collegate al sistema misto. Un collegamento ad un nodo acque luride/miste è obblicgatorio, mentre il collegamente ad un nodo acque meteoriche non è ammesso.','');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5188,'catchment_area','drainage_system_current','modified_system','modified_system','','ModifiziertesSystem','','systeme_modifie','','sistema_modificato','','','','yyy_Im Unterschied zum reinen Trennsystem, ist beim modifizierten System nicht nur der minimale Anteil an „verschmutztem“ Regenabwasser auf die Abwasserreinigungsanlage abgeleitet (gemäss der aktuellen Gewässerschutzgesetzgebung vorgeschriebenen Anteil) sondern zusätzlich auch noch ein Teil „nicht verschmutztes“ Regenabwasser. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.','Im Unterschied zum reinen Trennsystem, ist beim modifizierten System nicht nur der minimale Anteil an „verschmutztem“ Regenabwasser auf die Abwasserreinigungsanlage abgeleitet (gemäss der aktuellen Gewässerschutzgesetzgebung vorgeschriebenen Anteil) sondern zusätzlich auch noch ein Teil „nicht verschmutztes“ Regenabwasser. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.','Contrairement au système séparatif standard, le système modifié contient non seulement la partie minimale des eaux pluviales „polluées“ amenée vers la STEP (partie minimale selon la législation de la protection des cours d’eau en vigueur), mais encore une partie supplémentaire d’eau pluviale « non polluée ». La liaison avec les nœuds EP/EM est obligatoire, celle avec le nœud EP est admise.','zzz_Acque luride e acque meteoriche inquinate sono da collegare al sistema acque miste; acque meteoriche non inquinate che non può essere infiltrata alla rete acque meteoriche.','rrr_Im Unterschied zum reinen Trennsystem, ist beim modifizierten System nicht nur der minimale Anteil an „verschmutztem“ Regenabwasser auf die Abwasserreinigungsanlage abgeleitet (gemäss der aktuellen Gewässerschutzgesetzgebung vorgeschriebenen Anteil) sondern zusätzlich auch noch ein Teil „nicht verschmutztes“ Regenabwasser. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5185,'catchment_area','drainage_system_current','not_connected','not_connected','','nicht_angeschlossen','','non_raccorde','','non_collegato','','','','yyy_Teileinzugsgebiet das entwässert wird, aber nicht an eine PAA angeschlossen ist. Z.B. eine Fläche, die über eine SAA direkt in ein Gewässer entwässert wird, oder eine Fläche mit Versickerung über die Schulter. Keine Verbindung mit dem Kanalnetz zulässig.','Teileinzugsgebiet das entwässert wird, aber nicht an eine PAA angeschlossen ist. Z.B. eine Fläche, die über eine SAA direkt in ein Gewässer entwässert wird, oder eine Fläche mit Versickerung über die Schulter. Keine Verbindung mit dem Kanalnetz zulässig.','Un bassin versant assaini mais non raccordé à un OAP, par exemple une surface directement raccordée vers un cours d’eau via un OAS ou une surface avec de l’infiltration par les bas côtés. Aucune liaison possible avec le réseau des canalisations.','Area tributaria da cui l''acqua viene smaltita, ma che anche in futuro non viene collegata ad un impianto IPS. P. es un''area collegata da un ISS ad un ricettore o un''area con infiltrazione sulla banchina. Un collegamento con la rete di canalizzazioni non è ammissibile.','');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5537,'catchment_area','drainage_system_current','not_drained','not_drained','','nicht_entwaessert','','non_evacue','','non_evacuato','','','','yyy_Entwässerungstechnisch (noch) nicht erschlossene Fläche innerhalb des öffentlichen Kanalisationsbereichs. Z.B. noch nicht überbaute Parzelle innerhalb der Bauzone. Keine Verbindung mit dem Kanalnetz zulässig.','Entwässerungstechnisch (noch) nicht erschlossene Fläche innerhalb des öffentlichen Kanalisationsbereichs. Z.B. noch nicht überbaute Parzelle innerhalb der Bauzone. Keine Verbindung mit dem Kanalnetz zulässig.','Surface encore non raccordée d’un point de vue de l’assainissement, à l’intérieur du périmètre d’assainissement, par exemple des parcelles non construites en zone à bâtir. Aucune liaison possible avec le réseau des canalisations.','Area all''interno della zona servita che anche in futuro non sarà collegatra alla canalizzazione. Un collegamento alla rete di canalizzazione non è ammissibile.','');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5187,'catchment_area','drainage_system_current','separated_system','separated_system','','Trennsystem','','systeme_separatif','','sistema_separato','','rrr_Trennsystem','','yyy_Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz angeschlossen, nicht verschmutztes Regenabwasser an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.','Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz angeschlossen, nicht verschmutztes Regenabwasser an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.','Système d’évacuation des eaux comprenant en principe deux systèmes de réseaux de canalisations pour l’évacuation séparée des eaux usées et des eaux pluviales. Les eaux usées et les eaux pluviales considérées comme polluées par la législation en vigueur sont raccordées au réseau des eaux usées, tandis que les eaux pluviales non polluées sont raccordées au réseau des eaux pluviales. La liaison avec les nœuds EU/ est obligatoire, celle avec le nœud EP est admise.','zzz_Sistema di smaltimento usualmente composto da due reti di canalizzazione per lo smaltimento separato di acque luride  e meteoriche. Le acque luride sono da collegare alla rete acque luride, le acque meteoriche alla canalizzazione acque meteoriche. Almeno un collegamento alla rete acque luride/miste o acque meteoriche è obbligatorio.','rrr_Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz angeschlossen, nicht verschmutztes Regenabwasser an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten zulässig.');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5184,5189,'catchment_area','drainage_system_current','unknown','unknown','','unbekannt','','inconnu','','sconosciuto','','','',' yyy_Das Entwässerungssystem ist noch nicht bekannt. Dieser Wert ist nur bei einer noch nicht abgeschlossenen GEP-Bearbeitung zulässig. Keine Regeln bezüglich Verbindung zum Kanalnetz.','Das Entwässerungssystem ist noch nicht bekannt. Dieser Wert ist nur bei einer noch nicht abgeschlossenen GEP-Bearbeitung zulässig. Keine Regeln bezüglich Verbindung zum Kanalnetz.','Cette valeur n’est admise que lors de l''élaboration d’un PGEE. Aucune règle quant aux liaisons avec le réseau des canalisations.','Questo valore è ammesso unicamente durante l''elaborazione PGS. Nessuna regola relativa a collegamenti alla rete di smaltimento.','');
 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5190,5191,'catchment_area','drainage_system_planned','mixed_system','mixed_system','','Mischsystem','','systeme_unitaire','','sistema_misto','','','','yyy_Das Schmutzabwasser und das nicht versickerbare Regenabwasser sind an das Mischabwassernetz anzuschliessen. Verbindung zu SW/MW-Knoten obligatorisch, Verbindung zu RW-Knoten nicht zulässig.','Das Schmutzabwasser und das nicht versickerbare Regenabwasser sind an das Mischabwassernetz anzuschliessen. Verbindung zu SW/MW-Knoten obligatorisch, Verbindung zu RW-Knoten nicht zulässig.','Les eaux usées, ainsi que les eaux pluviales ne pouvant pas être infiltrées sont à raccorder au réseau d’évacuation des eaux mixtes. La liaison avec les nœuds EU/EM est obligatoire, la liaison avec le nœud EP n’est pas admise.','Le acque luride e le acque meteoriche non infiltrabili essere collegate al sistema misto. Un collegamento ad un nodo acque luride/miste è obbligatorio, mentre il collegamento ad un nodo acque meteoriche non è ammesso.','rrr_Das Schmutzabwasser und das nicht versickerbare Regenabwasser sind an das Mischabwassernetz anzuschliessen. Verbindung zu SW/MW-Knoten obligatorisch, Verbindung zu RW-Knoten nicht zulässig.');
