 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (19,5190,5192,'catchment_area','drainage_system_planned','separated_system','separated_system','','Trennsystem','','systeme_separatif','','sistema_separato','','','','yyy_Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz anzuschliessen; nicht verschmutztes Regenabwasser, das nicht versickert werden kann, an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten ist zulässig.','Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz anzuschliessen; nicht verschmutztes Regenabwasser, das nicht versickert werden kann, an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten ist zulässig.','Système d’évacuation des eaux comprenant en principe deux systèmes de réseaux de canalisations pour l’évacuation séparée des eaux usées et des eaux pluviales. Les eaux usées, ainsi que les eaux pluviales, considérées par la législation en vigueur comme polluées, sont à raccorder au réseau des eaux usées. Les eaux pluviales ne pouvant pas être infiltrées, sont à raccorder au réseau des eaux pluviales. La liaison avec les nœuds EU/EM est obligatoire, celle avec le nœud EP est admise.','zzz_Sistema di smaltimento usualmente composto da due reti di canalizzazione per lo smaltimento separato di acque luride  e meteoriche. Le acque luride sono da collegare alla rete acque luride, le acque meteoriche alla canalizzazione acque meteoriche. Almeno un collegamento alla rete acque luride/miste o acque meteoriche è obbligatorio.','rrr_Entwässerungssystem, üblicherweise bestehend aus zwei Leitungs-/Kanalsystemen für die getrennte Ableitung von Schmutz- und Regenabwasser. Schmutzabwasser und Regenabwasser welches gemäss aktueller Gesetzgebung als verschmutzt gilt, sind an das Schmutzabwassernetz anzuschliessen; nicht verschmutztes Regenabwasser, das nicht versickert werden kann, an das Regenabwassernetz. Verbindung zu SW/MW-Knoten ist obligatorisch. Verbindung zu RW-Knoten ist zulässig.');
