 INSERT INTO qgep_sys.dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_name_ro, shortcut_ro, value_description_en, value_description_de, value_description_fr, value_description_it, value_description_ro) VALUES (2,181,5368,'wastewater_structure','renovation_necessity','none','none','N','keiner','K','aucune','AN','nessuno','','niciuna','','yyy_Es sind keine Massnahmen bis zur nächsten Zustandserfassung und Zustandsbeurteilung erforderlich. Zeithorizont >= 10 Jahre.','Es sind keine Massnahmen bis zur nächsten Zustandserfassung und Zustandsbeurteilung erforderlich. Zeithorizont >= 10 Jahre.','Aucune mesure n’est nécessaire jusqu’au prochain relevé et à la prochaine évaluation de l’état. Délai >= 10 ans','Non sono necessari interventi fino al prossimo rilevamento dello stato e relativa valutazione. Orizzonte >=10 anni','Nu este necesara nici o masura pana la urmatoarea verificare. De la 10 ani in sus.');
