    , NEW.fk_provider
    , NEW.obj_id
  );

  UPDATE qgep_od.wastewater_structure
  SET fk_main_cover = NEW.co_obj_id
  WHERE obj_id = NEW.obj_id;

  RETURN NEW;
END; $$;


