--
-- Name: vw_network_node; Type: MATERIALIZED VIEW; Schema: qgep_od; Owner: postgres
--

CREATE MATERIALIZED VIEW qgep_od.vw_network_node AS
 WITH cover_levels_per_network_element AS (
         SELECT ne_1.obj_id,
            max(co_1.level) AS level
           FROM (((qgep_od.cover co_1
             JOIN qgep_od.structure_part sp ON (((co_1.obj_id)::text = (sp.obj_id)::text)))
             JOIN qgep_od.wastewater_structure ws_1 ON (((sp.fk_wastewater_structure)::text = (ws_1.obj_id)::text)))
             JOIN qgep_od.wastewater_networkelement ne_1 ON (((ws_1.obj_id)::text = (ne_1.fk_wastewater_structure)::text)))
          GROUP BY ne_1.obj_id
        )
 SELECT n.id AS gid,
        CASE
            WHEN (n.node_type = 'reach_point'::text) THEN n.rp_id
            WHEN (n.node_type = 'wastewater_node'::text) THEN n.ne_id
            ELSE ((n.ne_id || '-BLIND-'::text) || row_number() OVER (PARTITION BY n.node_type, n.ne_id ORDER BY (public.st_x(n.geom))))
        END AS obj_id,
    n.node_type AS type,
        CASE
            WHEN (n.node_type = 'reach_point'::text) THEN 'reach_point'::text
            WHEN (mh.obj_id IS NOT NULL) THEN 'manhole'::text
            WHEN (ws.obj_id IS NOT NULL) THEN 'special_structure'::text
            ELSE 'other'::text
        END AS node_type,
        CASE
            WHEN (n.node_type = 'reach_point'::text) THEN rp.level
            WHEN (n.node_type = 'wastewater_node'::text) THEN nd.bottom_level
            ELSE NULL::numeric
        END AS level,
    NULL::text AS usage_current,
    co.level AS cover_level,
    NULL::double precision AS backflow_level,
        CASE
            WHEN (n.node_type = 'reach_point'::text) THEN (rp.identifier)::text
            WHEN (n.node_type = 'wastewater_node'::text) THEN (ne.identifier)::text
            ELSE (((ne.identifier)::text || '-BLIND-'::text) || row_number() OVER (PARTITION BY n.node_type, n.ne_id ORDER BY (public.st_x(n.geom))))
        END AS description,
    n.geom AS situation_geometry,
    n.geom AS detail_geometry
   FROM ((((((qgep_network.node n
     LEFT JOIN qgep_od.reach_point rp ON ((n.rp_id = (rp.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_networkelement ne ON ((n.ne_id = (ne.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_node nd ON ((n.ne_id = (nd.obj_id)::text)))
     LEFT JOIN cover_levels_per_network_element co ON (((n.ne_id = (co.obj_id)::text) AND (n.node_type = 'wastewater_node'::text))))
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN qgep_od.manhole mh ON (((mh.obj_id)::text = (ws.obj_id)::text)))
  WITH NO DATA;


ALTER TABLE qgep_od.vw_network_node OWNER TO postgres;

--
-- Name: vw_network_segment; Type: MATERIALIZED VIEW; Schema: qgep_od; Owner: postgres
--

CREATE MATERIALIZED VIEW qgep_od.vw_network_segment AS
 SELECT s.id AS gid,
    COALESCE(s.ne_id,
        CASE
            WHEN (n1.type = 'wastewater_node'::text) THEN n1.obj_id
            WHEN (n2.type = 'wastewater_node'::text) THEN n2.obj_id
            ELSE ''::text
        END) AS obj_id,
    s.segment_type AS type,
    r.clear_height,
    public.st_length(s.geom) AS length_calc,
    public.st_length(r.progression_geometry) AS length_full,
    n1.obj_id AS from_obj_id,
    n2.obj_id AS to_obj_id,
    n1.obj_id AS from_obj_id_interpolate,
    n2.obj_id AS to_obj_id_interpolate,
    0 AS from_pos,
    1 AS to_pos,
        CASE
            WHEN (s.segment_type = 'reach'::text) THEN NULL::numeric
            WHEN ((n1.level IS NOT NULL) AND (n2.level IS NOT NULL)) THEN GREATEST(n1.level, n2.level)
            ELSE COALESCE(n1.level, n2.level)
        END AS bottom_level,
    ch.usage_current,
    mat.abbr_de AS material,
    s.geom AS progression_geometry,
    s.geom AS detail_geometry
   FROM ((((((qgep_network.segment s
     JOIN qgep_od.vw_network_node n1 ON ((n1.gid = s.from_node)))
     JOIN qgep_od.vw_network_node n2 ON ((n2.gid = s.to_node)))
     LEFT JOIN qgep_od.reach r ON (((r.obj_id)::text = s.ne_id)))
     LEFT JOIN qgep_vl.reach_material mat ON ((r.material = mat.code)))
     LEFT JOIN qgep_od.wastewater_networkelement ne ON (((ne.obj_id)::text = s.ne_id)))
     LEFT JOIN qgep_od.channel ch ON (((ch.obj_id)::text = (ne.fk_wastewater_structure)::text)))
  WITH NO DATA;


