--
-- Name: vw_conduits; Type: VIEW; Schema: qgep_swmm; Owner: postgres
--

CREATE VIEW qgep_swmm.vw_conduits AS
 SELECT re.obj_id AS name,
    COALESCE(from_wn.obj_id, 'default_qgep_node'::character varying) AS fromnode,
    COALESCE(to_wn.obj_id, 'default_qgep_node'::character varying) AS tonode,
        CASE
            WHEN ((re.length_effective <= 0.01) AND (public.st_length(re.progression_geometry) <= (0.01)::double precision)) THEN (0.01)::double precision
            WHEN ((re.length_effective <= 0.01) AND (public.st_length(re.progression_geometry) >= (0.01)::double precision)) THEN public.st_length(re.progression_geometry)
            WHEN ((re.length_effective IS NULL) AND (public.st_length(re.progression_geometry) <= (0.01)::double precision)) THEN (0.01)::double precision
            WHEN ((re.length_effective IS NULL) AND (public.st_length(re.progression_geometry) >= (0.01)::double precision)) THEN public.st_length(re.progression_geometry)
            ELSE (re.length_effective)::double precision
        END AS length,
    COALESCE(re.wall_roughness, 0.01) AS roughness,
    COALESCE((rp_from.level - from_wn.bottom_level), (0)::numeric) AS inletoffset,
    COALESCE((rp_to.level - to_wn.bottom_level), (0)::numeric) AS outletoffset,
    0 AS initflow,
    0 AS maxflow,
    (ws.identifier)::text AS description,
    cfh.value_en AS tag,
    (public.st_simplifypreservetopology(public.st_curvetoline(re.progression_geometry), (0.5)::double precision))::public.geometry(LineStringZ,2056) AS geom,
        CASE
            WHEN (ws.status = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state
   FROM ((((((((qgep_od.reach re
     LEFT JOIN qgep_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (re.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN qgep_od.reach_point rp_from ON (((rp_from.obj_id)::text = (re.fk_reach_point_from)::text)))
     LEFT JOIN qgep_od.reach_point rp_to ON (((rp_to.obj_id)::text = (re.fk_reach_point_to)::text)))
     LEFT JOIN qgep_od.wastewater_node from_wn ON (((from_wn.obj_id)::text = (rp_from.fk_wastewater_networkelement)::text)))
     LEFT JOIN qgep_od.wastewater_node to_wn ON (((to_wn.obj_id)::text = (rp_to.fk_wastewater_networkelement)::text)))
     LEFT JOIN qgep_od.channel ch ON (((ch.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN qgep_vl.channel_function_hydraulic cfh ON ((cfh.code = ch.function_hydraulic)))
  WHERE ((ch.function_hierarchic = ANY (ARRAY[5066, 5068, 5069, 5070, 5064, 5071, 5062, 5072, 5074])) AND (ws.status = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])));


ALTER TABLE qgep_swmm.vw_conduits OWNER TO postgres;

--
-- Name: vw_junctions; Type: VIEW; Schema: qgep_swmm; Owner: postgres
--

CREATE VIEW qgep_swmm.vw_junctions AS
 SELECT wn.obj_id AS name,
    COALESCE(wn.bottom_level, (0)::numeric) AS invertelev,
    (co.level - wn.bottom_level) AS maxdepth,
    NULL::double precision AS initdepth,
    NULL::double precision AS surchargedepth,
    NULL::double precision AS pondedarea,
    (ws.identifier)::text AS description,
    concat_ws(','::text, 'manhole', mf.value_en) AS tag,
    wn.situation_geometry AS geom,
        CASE
            WHEN (ws.status = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state
   FROM (((((qgep_od.manhole ma
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ma.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_networkelement we ON (((we.fk_wastewater_structure)::text = (ws.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_node wn ON (((wn.obj_id)::text = (we.obj_id)::text)))
     LEFT JOIN qgep_od.cover co ON (((ws.fk_main_cover)::text = (co.obj_id)::text)))
     LEFT JOIN qgep_vl.manhole_function mf ON ((ma.function = mf.code)))
  WHERE ((wn.obj_id IS NOT NULL) AND (ws._function_hierarchic = ANY (ARRAY[5066, 5068, 5069, 5070, 5064, 5071, 5062, 5072, 5074])) AND (ws.status = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])))
UNION
 SELECT wn.obj_id AS name,
    COALESCE(wn.bottom_level, (0)::numeric) AS invertelev,
    (co.level - wn.bottom_level) AS maxdepth,
    NULL::double precision AS initdepth,
    NULL::double precision AS surchargedepth,
    NULL::double precision AS pondedarea,
    (ws.identifier)::text AS description,
    concat_ws(','::text, 'special_structure', ssf.value_en) AS tag,
    wn.situation_geometry AS geom,
        CASE
            WHEN (ws.status = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state
   FROM (((((qgep_od.special_structure ss
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ss.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_networkelement we ON (((we.fk_wastewater_structure)::text = (ws.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_node wn ON (((wn.obj_id)::text = (we.obj_id)::text)))
     LEFT JOIN qgep_od.cover co ON (((ws.fk_main_cover)::text = (co.obj_id)::text)))
     LEFT JOIN qgep_vl.special_structure_function ssf ON ((ss.function = ssf.code)))
  WHERE ((wn.obj_id IS NOT NULL) AND (ws._function_hierarchic = ANY (ARRAY[5066, 5068, 5069, 5070, 5064, 5071, 5062, 5072, 5074])) AND (ws.status = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])) AND (ss.function <> ALL (ARRAY[6397, 6398, 3348, 336, 6478, 3673, 3674, 5574, 3675, 3676, 5575, 5576, 3677])))
UNION
 SELECT from_wn.obj_id AS name,
    COALESCE(from_wn.bottom_level, (0)::numeric) AS invertelev,
    0 AS maxdepth,
    NULL::double precision AS initdepth,
    NULL::double precision AS surchargedepth,
    NULL::double precision AS pondedarea,
    from_wn.obj_id AS description,
    'junction without structure'::text AS tag,
    from_wn.situation_geometry AS geom,
        CASE
            WHEN (ws.status = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state
   FROM (((((((qgep_od.reach re
     LEFT JOIN qgep_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (re.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN qgep_od.reach_point rp_from ON (((rp_from.obj_id)::text = (re.fk_reach_point_from)::text)))
     LEFT JOIN qgep_od.wastewater_node from_wn ON (((from_wn.obj_id)::text = (rp_from.fk_wastewater_networkelement)::text)))
     LEFT JOIN qgep_od.channel ch ON (((ch.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_networkelement we ON (((from_wn.obj_id)::text = (we.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_structure ws_node ON (((we.fk_wastewater_structure)::text = (ws_node.obj_id)::text)))
  WHERE ((ch.function_hierarchic = ANY (ARRAY[5066, 5068, 5069, 5070, 5064, 5071, 5062, 5072, 5074])) AND (ws.status = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])) AND (ws_node.* IS NULL))
