--
-- Name: vw_xsections; Type: VIEW; Schema: qgep_swmm; Owner: postgres
--

CREATE VIEW qgep_swmm.vw_xsections AS
 SELECT DISTINCT re.obj_id AS link,
        CASE
            WHEN (pp.profile_type = 3350) THEN 'CIRCULAR'::text
            WHEN (pp.profile_type = 3353) THEN 'RECT_CLOSED'::text
            WHEN (pp.profile_type = 3351) THEN 'EGG'::text
            WHEN (pp.profile_type = 3355) THEN 'CUSTOM'::text
            WHEN (pp.profile_type = 3352) THEN 'ARCH'::text
            WHEN (pp.profile_type = 3354) THEN 'PARABOLIC'::text
            ELSE 'CIRCULAR'::text
        END AS shape,
        CASE
            WHEN (re.clear_height = 0) THEN (0.1)::double precision
            WHEN (re.clear_height IS NULL) THEN (0.1)::double precision
            ELSE ((re.clear_height)::double precision / (1000)::double precision)
        END AS geom1,
    0 AS geom2,
    0 AS geom3,
    0 AS geom4,
    1 AS barrels,
    NULL::text AS culvert,
        CASE
            WHEN (ws.status = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state
   FROM ((((qgep_od.reach re
     LEFT JOIN qgep_od.pipe_profile pp ON (((pp.obj_id)::text = (re.fk_pipe_profile)::text)))
     LEFT JOIN qgep_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (re.obj_id)::text)))
     LEFT JOIN qgep_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN qgep_od.channel ch ON (((ch.obj_id)::text = (ws.obj_id)::text)))
  WHERE ((ch.function_hierarchic = ANY (ARRAY[5066, 5068, 5069, 5070, 5064, 5071, 5062, 5072, 5074])) AND (ws.status = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])));


